func (c *ClusterTx) GetInstanceByProject(project string, filter InstanceFilter) (*Instance, error) {
	objects, err := c.GetInstancesByProject(project, filter)
	if err != nil {
		return nil, errors.Wrap(err, "Failed to fetch Instance")
	}

	switch len(objects) {
	case 0:
		return nil, ErrNoSuchObject
	case 1:
		return &objects[0], nil
	default:
		return nil, fmt.Errorf("More than one instance matches")
	}
}

